// Define training data
const xs = tf.tensor1d([1, 2, 3, 4]);
const ys = tf.tensor1d([1, 3, 5, 7]);

// Define the model
const model = tf.sequential();
model.add(tf.layers.dense({units: 1, inputShape: [1]}));

// Compile the model
model.compile({
  optimizer: 'sgd',
  loss: 'meanSquaredError'
});

// Train the model
model.fit(xs, ys, {epochs: 500}).then(() => {
  // Use the model to predict
  model.predict(tf.tensor2d([5], [1, 1])).print();
});

const ys = tf.tensor1d([2, 2, 4, 3, 4.5, 4.5, 7, 10]);
const xs = tf.tensor1d([1, 2, 3, 4, 5, 6, 7, 8]);

let m = tf.scalar(Math.random()).variable();
let b = tf.scalar(Math.random()).variable();

const model = x => m.mul(x).add(b);
const learningRate = 0.01;
const optimizer = tf.train.sgd(learningRate);
const loss = (pred, actual) => pred.sub(actual).square().mean();

for (let i = 0; i < 10; ++i) {
  optimizer.minimize(() => loss(model(xs), ys));
}

const singlePrediction = model(2.3).dataSync();
const arrayPrediction = model(tf.tensor1d([2.3, 11, 100])).dataSync();

let mobilenet;
let video;
let label = '';

function modelReady() {
  console.log('Model is ready!!!');
  mobilenet.predict(gotResults);
}

function gotResults(error, results) {
  if (error) {
    console.error(error);
  } else {
    label = results[0].className;
    mobilenet.predict(gotResults);
  }
}

function setup() {
  createCanvas(640, 550);
  video = createCapture(VIDEO);
  video.hide();
  background(0);
  mobilenet = ml5.imageClassifier('MobileNet', video, modelReady);
}

function draw() {
  background(0);
  image(video, 0, 0);
  fill(255);
  textSize(32);
  text(label, 10, height - 20);
}
